mapper.insert:
INSERT INTO BBS_TBL VALUES(
	BBS_SEQ.NEXTVAL
	, #{subject}
	, #{content}
	, #{writer}
	, SYSDATE
	, 0
)

INSERT INTO REPLY_TBL VALUES(
	REPLY_SEQ.NEXTVAL
	, #{seq}
	, #{rWriter}
	, #{rContent}
	, SYSDATE
)

mapper.update:
UPDATE BBS_TBL SET
	SUBJECT = #{subject}
	, CONTENT = #{content}
	, WRITER = #{writer}
	WHERE SEQ = #{seq}
